import React, { useState } from 'react';

const validate = (userData, setErrors, errors) => {
  if (!userData.email)  setErrors({...errors, email: "Email vacio"});
  else{
  if (/^\w+([\.-]?\w+)@\w+([\.-]?\w+)(\.\w{2,3})+$/.test(userData.email))
  setErrors({...errors, email: ""});
  else setErrors({ ...errors, email: "Email inv치lido" });
  }
}

const validatePassword = (userData, setErrors, errors) => {
  if (!userData.password)  setErrors({...errors, password: "Ingrese una constrase침a."});
  else{
  if (/^(?=.\d)(?=.[a-z])(?=.*[A-Z]).{4,8}$/.test(userData.password))
  setErrors({...errors, password: ""});
  else setErrors({ ...errors, password: "Password invalido" });


}
}


